import React, { useState } from "react";
import axios from "axios";

const LeaveForm = () => {
  const [formData, setFormData] = useState({
    startdate: "",
    enddate: "",
    reason: "",
    type: "",
    to: ""
  });

  const [successMessage, setSuccessMessage] = useState(null);
  const [errorMessage, setErrorMessage] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Post the form data to the backend
      const response = await axios.post("http://localhost:3005/leaves", formData);
      setSuccessMessage("Leave request submitted successfully!");
      setErrorMessage(null);
      setFormData({
        startdate: "",
        enddate: "",
        reason: "",
        type: "",
        to: ""
      });
    } catch (err) {
      setErrorMessage("Error submitting the leave request. Please try again.");
      setSuccessMessage(null);
    }
  };

  return (
    <div className="p-10 bg-gradient-to-b from-blue-500 via-purple-500 to-pink-500 min-h-screen flex items-center justify-center">
      <div className="bg-white p-8 rounded-lg shadow-lg max-w-lg w-full">
        <h2 className="text-3xl font-bold mb-6 text-center text-purple-600">Leave Request</h2>

        {successMessage && <p className="text-center text-green-600 mb-4">{successMessage}</p>}
        {errorMessage && <p className="text-center text-red-600 mb-4">{errorMessage}</p>}

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Start Date */}
          <div>
            <label className="block mb-1 text-lg font-semibold text-purple-600">Start Date</label>
            <input
              type="date"
              name="startdate"
              value={formData.startdate}
              onChange={handleChange}
              required
              className="w-full p-3 border border-purple-300 rounded-lg focus:ring-2 focus:ring-purple-500"
            />
          </div>

          {/* End Date */}
          <div>
            <label className="block mb-1 text-lg font-semibold text-purple-600">End Date</label>
            <input
              type="date"
              name="enddate"
              value={formData.enddate}
              onChange={handleChange}
              required
              className="w-full p-3 border border-purple-300 rounded-lg focus:ring-2 focus:ring-purple-500"
            />
          </div>

          {/* Reason */}
          <div>
            <label className="block mb-1 text-lg font-semibold text-purple-600">Reason</label>
            <textarea
              name="reason"
              value={formData.reason}
              onChange={handleChange}
              required
              className="w-full p-3 border border-purple-300 rounded-lg focus:ring-2 focus:ring-purple-500"
              placeholder="Enter your reason"
            ></textarea>
          </div>

          {/* Type */}
          <div>
            <label className="block mb-1 text-lg font-semibold text-purple-600">Type of Leave</label>
            <select
              name="type"
              value={formData.type}
              onChange={handleChange}
              required
              className="w-full p-3 border border-purple-300 rounded-lg focus:ring-2 focus:ring-purple-500"
            >
              <option value="">Select Leave Type</option>
              <option value="Sick">Sick</option>
              <option value="Casual">Casual</option>
              <option value="Vacation">Vacation</option>
              <option value="Maternity">Maternity</option>
            </select>
          </div>

          {/* To */}
          <div>
            <label className="block mb-1 text-lg font-semibold text-purple-600">To</label>
            <input
              type="text"
              name="to"
              value={formData.to}
              onChange={handleChange}
              required
              className="w-full p-3 border border-purple-300 rounded-lg focus:ring-2 focus:ring-purple-500"
              placeholder="Enter the recipient's name"
            />
          </div>

          <button
            type="submit"
            className="w-full bg-purple-600 text-white p-3 rounded-lg shadow-lg hover:bg-purple-700 transition duration-300 font-bold"
          >
            Submit Leave Request
          </button>
        </form>
      </div>
    </div>
  );
};

export default LeaveForm;
